let themeDots=document.getElementsByClassName("theme-dot"),theme=localStorage.getItem("theme");setTheme(null==theme?"light":theme);for(let e=0;themeDots.length>e;e++)themeDots[e].addEventListener("click",function(){setTheme(this.dataset.mode)});function setTheme(e){"light"==e&&(document.getElementById("theme-style").href="styles/default.css"),"blue"==e&&(document.getElementById("theme-style").href="styles/blue.css"),"green"==e&&(document.getElementById("theme-style").href="styles/green.css"),"purple"==e&&(document.getElementById("theme-style").href="styles/purple.css"),localStorage.setItem("theme",e)}